+import type {TFollowerGroup, TPartitionConfig, TTableStats} from '../../../types/api/schema';
+import type {TMetrics} from '../../../types/api/tenant';
+import {
+    formatBps,
+    formatCPU,
+    formatDateTime,
+    formatNumber,
+} from '../../../utils/dataFormatters/dataFormatters';
+import {toFormattedSize} from '../../FormattedBytes/utils';
+import {createDefinitionFormatter} from '../utils';
+
+export const formatTabletMetricsItem = createDefinitionFormatter<TMetrics>({
+    contents: {
+        CPU: formatCPU,
+        Memory: toFormattedSize,
+        Storage: toFormattedSize,
+        Network: formatBps,
+        ReadThroughput: formatBps,
+        WriteThroughput: formatBps,
+    },
+    defaultContentFormatter: formatNumber,
+});
+
+export const formatFollowerGroupItem = createDefinitionFormatter<TFollowerGroup>({
+    contents: {
+        FollowerCount: formatNumber,
+    },
+    names: {
+        // Make it shorter to fit label width
+        FollowerCountPerDataCenter: 'FollowerCountPerDC',
+    },
+    // Most of the FollowerGroup fields are arrays or boolean
+    defaultContentFormatter: (value) => value && String(value),
+});
+
+export const formatPartitionConfigItem = createDefinitionFormatter<TPartitionConfig>({
+    contents: {
+        FollowerCount: formatNumber,
+        CrossDataCenterFollowerCount: formatNumber,
+    },
+});
+
+export const formatTableStatsItem = createDefinitionFormatter<TTableStats>({
+    contents: {
+        DataSize: toFormattedSize,
+        IndexSize: toFormattedSize,
+        LastAccessTime: formatDateTime,
+        LastUpdateTime: formatDateTime,
+    },
+    defaultContentFormatter: formatNumber,
+});
diff --git a/src/components/YDBDefinitionList/utils.ts b/src/components/YDBDefinitionList/utils.ts
new file mode 100644
index 00000000..e2e8ba3b
--- /dev/null
+++ b/src/components/YDBDefinitionList/utils.ts
@@ -0,0 +1,58 @@
+import type React from 'react';
+
+import type {YDBDefinitionListItem} from './YDBDefinitionList';
+
+type NameMap<T> = {
+    [key in keyof T]?: string;
+};
+
+type ContentFormatters<T> = {
+    [key in keyof T]?: (value: T[key]) => React.ReactNode;
+};
+
+function formatName<Shape>(key: keyof Shape, map: NameMap<Shape>) {
+    return map[key] ?? String(key);
+}
+
+function formatContent<Shape, Key extends keyof Shape>(
+    key: Key,
+    value: Shape[Key],
+    formatters: ContentFormatters<Shape>,
+    defaultFormatter?: (value: Shape[Key]) => React.ReactNode,
+) {
+    const formatter = formatters[key] || defaultFormatter;
+
+    return formatter ? formatter(value) : value;
+}
+
+interface CreateDefinitionFormatterOptions<Shape> {
+    contents?: ContentFormatters<Shape>;
+    names?: NameMap<Shape>;
+    defaultContentFormatter?: (value: Shape[keyof Shape]) => React.ReactNode;
+}
+
+export function createDefinitionFormatter<Shape extends Record<string, any>>({
+    contents,
+    names,
+    defaultContentFormatter,
+}: CreateDefinitionFormatterOptions<Shape>) {
+    return <Key extends keyof Shape>(key: Key, value: Shape[Key]): YDBDefinitionListItem => {
+        return {
+            name: formatName(key, names || {}),
+            content: formatContent(key, value, contents || {}, defaultContentFormatter),
+        };
+    };
+}
+
+export const formatObjectToDefinitionItems = <Shape extends Record<string, any>>(
+    formatter: <Key extends keyof Shape>(key: Key, value: Shape[Key]) => YDBDefinitionListItem,
+    obj?: Partial<Shape>,
+): YDBDefinitionListItem[] => {
+    if (!obj) {
+        return [];
+    }
+
+    return Object.entries(obj)
+        .map(([key, value]) => formatter(key as keyof Shape, value as Shape[keyof Shape]))
+        .filter(({content}) => Boolean(content));
+};
