+.editor-tab-item {
+    &__tab {
+        display: flex;
+        align-items: center;
+
+        width: 100%;
+        max-width: 200px;
+    }
+
+    // Override Gravity UI Tab to match design: borders, backgrounds, rounded corners.
+    &__tab-root {
+        position: relative;
+
+        overflow: hidden;
+
+        min-width: 60px;
+        max-width: 200px;
+        padding-bottom: 3px; // 1px for border, 2px for padding
+
+        border: 1px solid var(--multitab-tab-inactive-border-color);
+        border-bottom: 0;
+        border-radius: var(--g-border-radius-xs) var(--g-border-radius-xs) 0 0;
+        background: var(--multitab-tab-inactive-background-color);
+
+        &:hover,
+        &_menuOpen {
+            border-color: var(--multitab-tab-active-border-color);
+            background-color: var(--multitab-tab-active-background-color);
+        }
+
+        &.g-tab_active,
+        &.g-tab_active:hover {
+            border-color: var(--multitab-tab-active-border-color);
+            background-color: var(--multitab-tab-active-background-color);
+        }
+
+        .g-tab__content {
+            height: 100%;
+            padding: 2px var(--g-spacing-2);
+        }
+
+        .g-tab__title {
+            display: flex;
+            align-items: center;
+
+            min-width: 0;
+
+            font-family: var(--g-text-caption-font-family);
+            font-size: var(--g-text-caption-2-font-size);
+            font-weight: var(--g-text-caption-font-weight);
+            line-height: var(--g-text-caption-2-line-height);
+
+            color: var(--g-color-text-complementary);
+        }
+    }
+
+    &__tab-root:hover &__tab-dirty,
+    &__tab-root_menuOpen &__tab-dirty {
+        display: none;
+    }
+
+    &__tab-title {
+        overflow: hidden;
+        flex: 1 1 auto;
+
+        min-width: 0;
+
+        white-space: nowrap;
+        text-overflow: ellipsis;
+    }
+
+    &__tab-additional {
+        position: absolute;
+        top: 0;
+        right: 0;
+        bottom: 0;
+
+        display: flex;
+        align-items: center;
+
+        padding-right: 0;
+
+        pointer-events: none;
+
+        background-color: var(--multitab-tab-inactive-background-color);
+
+        &::before {
+            position: absolute;
+            top: 0;
+            right: 100%;
+            bottom: 0;
+
+            display: none;
+
+            width: 16px;
+
+            content: '';
+
+            background: linear-gradient(
+                to right,
+                transparent,
+                var(--multitab-tab-inactive-background-color)
+            );
+        }
+    }
+
+    &__tab-additional_dirty {
+        padding-right: var(--g-spacing-2);
+
+        &::before {
+            display: block;
+        }
+    }
+
+    &__tab-root.g-tab_active &__tab-additional_dirty {
+        background-color: var(--multitab-tab-active-background-color);
+
+        &::before {
+            background: linear-gradient(
+                to right,
+                transparent,
+                var(--multitab-tab-active-background-color)
+            );
+        }
+    }
+
+    &__tab-root:hover &__tab-additional,
+    &__tab-root_menuOpen &__tab-additional {
+        padding-right: var(--g-spacing-1);
+
+        background-color: var(--multitab-tab-active-background-color);
+    }
+
+    &__tab-root:hover &__tab-additional::before,
+    &__tab-root_menuOpen &__tab-additional::before {
+        display: block;
+
+        background: linear-gradient(
+            to right,
+            transparent,
+            var(--multitab-tab-active-background-color)
+        );
+    }
+
+    &__tab-root:hover &__tab-action,
+    &__tab-root_menuOpen &__tab-action {
+        display: flex;
+
+        pointer-events: auto;
+    }
+
+    &__tab-dirty {
+        flex-shrink: 0;
+
+        width: 6px;
+        height: 6px;
+        margin-left: var(--g-spacing-2);
+
+        border-radius: 50%;
+        background-color: var(--g-color-text-complementary);
+    }
+
+    &__tab-action {
+        display: none;
+        justify-content: center;
+        align-items: center;
+
+        width: 20px;
+        height: 20px;
+
+        cursor: pointer;
+
+        color: var(--g-color-text-secondary);
+        border-radius: var(--g-border-radius-xs);
+
+        &:hover {
+            background-color: var(--multitab-tab-action-hover-color);
+        }
+    }
+
+    &__tab-menu-switcher {
+        display: inline-flex;
+        align-items: center;
+    }
+}
diff --git a/src/containers/Tenant/Query/QueryEditor/EditorTabs/EditorTabItem.tsx b/src/containers/Tenant/Query/QueryEditor/EditorTabs/EditorTabItem.tsx
new file mode 100644
index 00000000..26f372b2
--- /dev/null
+++ b/src/containers/Tenant/Query/QueryEditor/EditorTabs/EditorTabItem.tsx
@@ -0,0 +1,236 @@
+import React from 'react';
+
+import {Copy, Ellipsis, FloppyDisk, FontCursor, Xmark} from '@gravity-ui/icons';
+import type {DropdownMenuItem} from '@gravity-ui/uikit';
+import {DropdownMenu, Flex, Hotkey, Icon, Tab, Text} from '@gravity-ui/uikit';
+
+import type {QueryTabState} from '../../../../../store/reducers/query/types';
+import {cn} from '../../../../../utils/cn';
+import i18n from '../../i18n';
+import {isQueryCancelledError} from '../../utils/isQueryCancelledError';
+
+import type {TabExecutionStatus} from './TabExecutionStatusIndicator';
+import {TabExecutionStatusIndicator} from './TabExecutionStatusIndicator';
+
+import './EditorTabItem.scss';
+
+const b = cn('editor-tab-item');
+
+const HOTKEY_LABELS = {
+    renameTab: 'mod+alt+r',
+    duplicateTab: 'mod+alt+c',
+    closeTab: 'mod+backspace',
+    closeOtherTabs: 'mod+alt+backspace',
+    closeAllTabs: 'mod+shift+backspace',
+    saveQueryAs: 'mod+shift+s',
+} as const;
+
+function getTabExecutionStatus(tab: QueryTabState): TabExecutionStatus | undefined {
+    const result = tab.result;
+    if (!result) {
+        return undefined;
+    }
+
+    if (result.isLoading) {
+        return 'in_progress';
+    }
+
+    if (result.error) {
+        return isQueryCancelledError(result.error) ? 'stopped' : 'failed';
+    }
+
+    return 'done';
+}
+
+export interface EditorTabItemProps {
+    tabId: string;
+    tab: QueryTabState;
+    isActive: boolean;
+    onActivate: (tabId: string) => void;
+    onCloseTab: (tabId: string) => void;
+    onDuplicateTab: (tabId: string) => void;
+    onRenameTab: (tabId: string, currentTitle: string) => void;
+    onSaveQueryAs: (tabId: string) => void;
+    onCloseOtherTabs: (tabId: string) => void;
+    onCloseAllTabs: () => void;
+}
+
+export function EditorTabItem({
+    tabId,
+    tab,
+    isActive,
+    onActivate,
+    onCloseTab,
+    onDuplicateTab,
+    onRenameTab,
+    onSaveQueryAs,
+    onCloseOtherTabs,
+    onCloseAllTabs,
+}: EditorTabItemProps) {
+    const title = tab.title || i18n('editor-tabs.default-title');
+    const isDirty = Boolean(tab.isDirty);
+    const executionStatus = getTabExecutionStatus(tab);
+    const [isMenuOpen, setIsMenuOpen] = React.useState(false);
+    const [isMenuClosing, setIsMenuClosing] = React.useState(false);
+    const tabRef = React.useRef<HTMLButtonElement>(null);
+
+    React.useEffect(() => {
+        if (isActive && tabRef.current) {
+            tabRef.current.scrollIntoView({block: 'nearest', inline: 'nearest'});
+        }
+    }, [isActive, executionStatus]);
+
+    const handleMenuSwitcherClick = React.useCallback(
+        (event: React.MouseEvent<HTMLElement>) => {
+            event.stopPropagation();
+            onActivate(tabId);
+        },
+        [onActivate, tabId],
+    );
+
+    const handleMenuOpenToggle = React.useCallback((open: boolean) => {
+        if (open) {
+            setIsMenuOpen(true);
+            setIsMenuClosing(false);
+        } else {
+            setIsMenuOpen(false);
+            setIsMenuClosing(true);
+        }
+    }, []);
+
+    const handleMenuTransitionOutComplete = React.useCallback(() => {
+        setIsMenuClosing(false);
+    }, []);
+
+    const handleCloseClick = React.useCallback(
+        (event: React.MouseEvent<HTMLElement>) => {
+            event.stopPropagation();
+            onCloseTab(tabId);
+        },
+        [onCloseTab, tabId],
+    );
+
+    const handleRenameClick = React.useCallback(() => {
+        onRenameTab(tabId, tab.title);
+    }, [onRenameTab, tab.title, tabId]);
+
+    const handleDuplicateClick = React.useCallback(() => {
+        onDuplicateTab(tabId);
+    }, [onDuplicateTab, tabId]);
+
+    const handleSaveQueryAsClick = React.useCallback(() => {
+        onSaveQueryAs(tabId);
+    }, [onSaveQueryAs, tabId]);
+
+    const handleCloseOtherTabsClick = React.useCallback(() => {
+        onCloseOtherTabs(tabId);
+    }, [onCloseOtherTabs, tabId]);
+
+    const handleCloseAllTabsClick = React.useCallback(() => {
+        onCloseAllTabs();
+    }, [onCloseAllTabs]);
+
+    const tabMenuItems = React.useMemo<DropdownMenuItem[][]>(() => {
+        return [
+            [
+                {
+                    text: i18n('editor-tabs.rename-query'),
+                    iconStart: <FontCursor />,
+                    iconEnd: <Hotkey value={HOTKEY_LABELS.renameTab} />,
+                    action: handleRenameClick,
+                },
+                {
+                    text: i18n('editor-tabs.duplicate'),
+                    iconStart: <Copy />,
+                    iconEnd: <Hotkey value={HOTKEY_LABELS.duplicateTab} />,
+                    action: handleDuplicateClick,
+                },
+            ],
+            [
+                {
+                    text: i18n('editor-tabs.save-query-as'),
+                    iconStart: <FloppyDisk />,
+                    iconEnd: <Hotkey value={HOTKEY_LABELS.saveQueryAs} />,
+                    action: handleSaveQueryAsClick,
+                },
+            ],
+            [
+                {
+                    text: i18n('editor-tabs.close'),
+                    iconStart: <Xmark />,
+                    iconEnd: <Hotkey value={HOTKEY_LABELS.closeTab} />,
+                    action: () => onCloseTab(tabId),
+                },
+                {
+                    text: i18n('editor-tabs.close-other-tabs'),
+                    iconStart: <Xmark />,
+                    iconEnd: <Hotkey value={HOTKEY_LABELS.closeOtherTabs} />,
+                    action: handleCloseOtherTabsClick,
+                },
+                {
+                    text: i18n('editor-tabs.close-all-tabs'),
+                    iconStart: <Xmark />,
+                    iconEnd: <Hotkey value={HOTKEY_LABELS.closeAllTabs} />,
+                    action: handleCloseAllTabsClick,
+                },
+            ],
+        ];
+    }, [
+        handleCloseAllTabsClick,
+        handleCloseOtherTabsClick,
+        handleDuplicateClick,
+        handleRenameClick,
+        handleSaveQueryAsClick,
+        onCloseTab,
+        tabId,
+    ]);
+
+    const renderTabMenuSwitcher = React.useCallback((props: React.HTMLAttributes<HTMLElement>) => {
+        return (
+            <span {...props} className={b('tab-action', {menu: true})}>
+                <Ellipsis />
+            </span>
+        );
+    }, []);
+
+    return (
+        <Tab
+            ref={tabRef}
+            value={tabId}
+            className={b('tab-root', {menuOpen: isMenuOpen || isMenuClosing})}
+        >
+            <Flex className={b('tab')} alignItems="center" gap={1}>
+                <TabExecutionStatusIndicator status={executionStatus} />
+                <Text variant="caption-2" className={b('tab-title')}>
+                    {title}
+                </Text>
+                <Flex className={b('tab-additional', {dirty: isDirty})} alignItems="center" gap={0}>
+                    {isDirty ? <div className={b('tab-dirty', {active: isActive})} /> : null}
+                    <span className={b('tab-action', {active: isActive})}>
+                        <DropdownMenu
+                            items={tabMenuItems}
+                            open={isMenuOpen}
+                            onOpenToggle={handleMenuOpenToggle}
+                            onSwitcherClick={handleMenuSwitcherClick}
+                            renderSwitcher={renderTabMenuSwitcher}
+                            switcherWrapperClassName={b('tab-menu-switcher')}
+                            popupProps={{
+                                placement: ['bottom-start', 'top-start'],
+                                floatingStyles: {width: 264},
+                                onTransitionOutComplete: handleMenuTransitionOutComplete,
+                            }}
+                            size="s"
+                            menuProps={{size: 'l'}}
+                        />
+                    </span>
+                    <span
+                        className={b('tab-action', {close: true, active: isActive})}
+                        onClick={handleCloseClick}
+                    >
+                        <Icon data={Xmark} size={12} />
+                    </span>
+                </Flex>
+            </Flex>
+        </Tab>
+    );
+}
